package com.zero.bean;

import java.io.Serializable;

public class GoodsFavorite implements Serializable {

	private static final long serialVersionUID = 1L;
	private int id;
	private int goods;
	private String goodsName;
	private String GoodsPictureUrl;
	private double price;
	private String BrandName;
	private String GoodsSamllCategoryName;
	private String GoodsInfo;
	private String GoodsStandard;
	
	public GoodsFavorite() {
		// TODO Auto-generated constructor stub
	}

	public GoodsFavorite(int id, int goods, String goodsName,
			String goodsPictureUrl, double price, String brandName,
			String goodsSamllCategoryName, String goodsInfo,
			String goodsStandard) {
		super();
		this.id = id;
		this.goods = goods;
		this.goodsName = goodsName;
		GoodsPictureUrl = goodsPictureUrl;
		this.price = price;
		BrandName = brandName;
		GoodsSamllCategoryName = goodsSamllCategoryName;
		GoodsInfo = goodsInfo;
		GoodsStandard = goodsStandard;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public int getGoods() {
		return goods;
	}

	public void setGoods(int goods) {
		this.goods = goods;
	}

	public String getGoodsName() {
		return goodsName;
	}

	public void setGoodsName(String goodsName) {
		this.goodsName = goodsName;
	}

	public String getGoodsPictureUrl() {
		return GoodsPictureUrl;
	}

	public void setGoodsPictureUrl(String goodsPictureUrl) {
		GoodsPictureUrl = goodsPictureUrl;
	}

	public double getPrice() {
		return price;
	}

	public void setPrice(double price) {
		this.price = price;
	}

	public String getBrandName() {
		return BrandName;
	}

	public void setBrandName(String brandName) {
		BrandName = brandName;
	}

	public String getGoodsSamllCategoryName() {
		return GoodsSamllCategoryName;
	}

	public void setGoodsSamllCategoryName(String goodsSamllCategoryName) {
		GoodsSamllCategoryName = goodsSamllCategoryName;
	}

	public String getGoodsInfo() {
		return GoodsInfo;
	}

	public void setGoodsInfo(String goodsInfo) {
		GoodsInfo = goodsInfo;
	}

	public String getGoodsStandard() {
		return GoodsStandard;
	}

	public void setGoodsStandard(String goodsStandard) {
		GoodsStandard = goodsStandard;
	}
	
}
