package com.zero.bean;

import java.io.Serializable;

public class FoodsShoppingCar implements Serializable {

	private static final long serialVersionUID = 1L;

	private int categoryId;
	private int foodsNumber;
	private Dish foods;
	private String foodsName;
	public FoodsShoppingCar(int categoryId, int foodsNumber, Dish foods) {
		super();
		this.categoryId = categoryId;
		this.foodsNumber = foodsNumber;
		this.foods = foods;
	}

	public FoodsShoppingCar() {
		// TODO Auto-generated constructor stub
	}

	public int getCategoryId() {
		return categoryId;
	}

	public String getFoodsName() {
		return foodsName;
	}

	public void setFoodsName(String foodsName) {
		this.foodsName = foodsName;
	}

	public void setCategoryId(int categoryId) {
		this.categoryId = categoryId;
	}

	public int getFoodsNumber() {
		return foodsNumber;
	}

	public void setFoodsNumber(int foodsNumber) {
		this.foodsNumber = foodsNumber;
	}

	public Dish getFoods() {
		return foods;
	}

	public void setFoods(Dish foods) {
		this.foods = foods;
	}
	
}
